package DatatypesAndVariables;

public class ArithmaticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=10;
		int b=20;
		
		//+operator
		System.out.println(a+b);
		
		//Subtraction
		System.out.println(a-b);
		
		//Multiplication *
		System.out.println(a*b);
		
		//Division
		System.out.println(b/a);
		
		// % Mode
		System.out.println(17%19);
	}

}
